parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "task_status": {
      "type": "string",
      "enum": ["success", "failed"],
      "description": "Status of the post analysis task"
    },
    "failed_cause": {
      "type": "string",
      "enum": ["login_needed", "bot_captcha", "page_unreachable"],
      "description": "Reason for task failure (only present when task_status is 'failed')"
    },
    "error_details": {
      "type": "string",
      "description": "Additional details about the error (optional for failed tasks)"
    },
    "post_analysis": {
      "type": "object",
      "description": "Analysis results of the forum post (only present when task_status is 'success')",
      "properties": {
        "post_visibility": {
          "type": "string",
          "enum": ["visible", "hidden", "partial"],
          "description": "Whether the post content is fully visible, hidden behind login, or partially visible"
        },
        "post_summary": {
          "type": "string",
          "description": "Analytical summary of the post content and context (max 500 characters)"
        },
        "screenshot_filename": {
          "type": "string",
          "description": "Filename of the captured screenshot"
        }
      },
      "required": [
        "post_visibility", 
        "post_summary",
        "screenshot_filename"
      ],
      "additionalProperties": false
    }
  },
  "required": ["task_status"],
  "if": {
    "properties": {
      "task_status": {
        "const": "success"
      }
    }
  },
  "then": {
    "required": ["post_analysis"],
    "not": {
      "required": ["failed_cause"]
    }
  },
  "else": {
    "if": {
      "properties": {
        "task_status": {
          "const": "failed"
        }
      }
    },
    "then": {
      "required": ["failed_cause"],
      "not": {
        "required": ["post_analysis"]
      }
    }
  },
  "additionalProperties": false
}

